namespace Fa\Bundle\AdBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Fa\Bundle\AdBundle\Repository\AdRepository;

/**
 * AdContactRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Janak Jadeja <janak@aspl.in>
 * @copyright 2014 Friday Media Group Ltd
 * @version 1.0
 */
class AdContactRepository extends EntityRepository
{
    const ALIAS = 'ac';

    /**
     * PrepareQueryBuilder.
     *
     * @param array $data Array of data.
     *
     * @return Doctrine\ORM\QueryBuilder The query builder.
     */
    public function getBaseQueryBuilder()
    {
        return $this->createQueryBuilder(self::ALIAS);
    }

    /**
     * Remove ad from contact by ad id.
     *
     * @param integer $adId Ad id.
     */
    public function removeByAdId($adId)
    {
        $contactAds = $this->getBaseQueryBuilder()
                          ->andWhere(self::ALIAS.'.ad = :adId')
                          ->setParameter('adId', $adId)
                          ->getQuery()
                          ->getResult();

        if ($contactAds) {
            foreach ($contactAds as $contactAd) {
                $this->_em->remove($contactAd);
            }
            $this->_em->flush();
        }
    }
}
