namespace Fa\Bundle\AdBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * Ad Short list repository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Samir Amrutya <samiram@aspl.in>
 * @copyright 2014 Friday Media Group Ltd
 * @version 1.0
 */
class AdShortlistRepository extends EntityRepository
{
    const ALIAS = 'ash';

    /**
     * PrepareQueryBuilder.
     *
     * @param array $data Array of data.
     *
     * @return Doctrine\ORM\QueryBuilder The query builder.
     */
    public function getBaseQueryBuilder()
    {
        return $this->createQueryBuilder(self::ALIAS);
    }

    /**
     * Remove ad from shortlist by ad id.
     *
     * @param integer $adId Ad id.
     */
    public function removeByAdId($adId)
    {
        $adShortlists = $this->getBaseQueryBuilder()
                            ->andWhere(self::ALIAS.'.ad = :adId')
                            ->setParameter('adId', $adId)
                            ->getQuery()
                            ->getResult();

        //remove shortlist ad from table.
        if ($adShortlists) {
            foreach ($adShortlists as $adShortlist) {
                $this->_em->remove($adShortlist);
            }
            $this->_em->flush();
        }
    }
}
