namespace Fa\Bundle\AdBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Fa\Bundle\AdBundle\Entity\Ad;
use Fa\Bundle\AdBundle\Entity\AdStatistics;

/**
 * AdStatisticsRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Samir Amrutya <samiram@aspl.in>
 * @copyright 2014 Friday Media Group Ltd
 * @version 1.0
 */
class AdStatisticsRepository extends EntityRepository
{

    /**
     * insert expiration ad entry in statistic table
     *
     * @param Ad $ad
     *
     * @param integer $expired_at
     */
    public function insertExpiredStat(Ad $ad, $expired_at)
    {
        $adStatistics = new AdStatistics();
        $adStatistics->setAd($ad);
        $adStatistics->setExpiresAt($expired_at);
        $this->getEntityManager()->persist($adStatistics);
        $this->getEntityManager()->flush();
    }
}
