namespace Fa\Bundle\AdBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Fa\Bundle\AdBundle\Repository\PrintDeadlineRepository;
use Fa\Bundle\EntityBundle\Repository\LocationGroupRepository;

/**
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Samir Amrutya <samiram@aspl.in>
 * @copyright  2014 Friday Media Group Ltd
 * @version 1.0
 */
class PrintDeadlineRuleRepository extends EntityRepository
{
    use \Fa\Bundle\CoreBundle\Search\Search;

    const ALIAS = 'pdr';

    /**
     * Prepare query builder.
     *
     * @param array $data Array of data.
     *
     * @return Doctrine\ORM\QueryBuilder The query builder.
     */
    public function getBaseQueryBuilder()
    {
        return $this->createQueryBuilder(self::ALIAS);
    }

    /**
     * Prepare query builder.
     *
     * @param array $data Array of data.
     *
     * @return Doctrine\ORM\QueryBuilder The query builder.
     */
    public function getLocationGroupsByPrintDeadlineId($id = null)
    {
        $locationGroups = array();

        if ($id) {
            if (!is_array($id)) {
                $id = array($id);
            }

            $id = array_filter($id);

            if (count($id)) {
                $queryBuilder = $this->getBaseQueryBuilder();
                $queryBuilder->select(PrintDeadlineRepository::ALIAS.'.id', LocationGroupRepository::ALIAS.'.name')
                             ->leftJoin(self::ALIAS.'.print_deadline', PrintDeadlineRepository::ALIAS)
                             ->leftJoin(self::ALIAS.'.location_group', LocationGroupRepository::ALIAS)
                             ->andWhere(self::ALIAS.'.print_deadline IN (' . implode(', ', $id) . ')');

                $rules = $queryBuilder->getQuery()->getArrayResult();
                foreach ($rules as $rule) {
                    $locationGroups[$rule['id']][] = $rule['name'];
                }
            }
        }

        return $locationGroups;
    }
}
