namespace Fa\Bundle\ContentBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Fa\Bundle\CoreBundle\Manager\CommonManager;
use Fa\Bundle\AdBundle\Solr\AdSolrFieldMapping;
use Fa\Bundle\EntityBundle\Repository\LocationRepository;

/**
 * Landing page popular search repository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Amit Limbadia <amitl@aspl.in>
 * @copyright 2014 Friday Media Group Ltd
 * @version 1.0
 */
class LandingPagePopularSearchRepository extends EntityRepository
{
    use \Fa\Bundle\CoreBundle\Search\Search;

    const ALIAS = 'lpps';

    /**
     * Prepare query builder.
     *
     * @param array $data Array of data.
     *
     * @return Doctrine\ORM\QueryBuilder The query builder
     */
    public function getBaseQueryBuilder()
    {
        return $this->createQueryBuilder(self::ALIAS);
    }

    /**
     * Remove landing page popular search based on landing page id.
     *
     * @param integer $landingPageId
     */
    public function removeRecordsByLandingPageId($landingPageId)
    {
        $this->createQueryBuilder(self::ALIAS)
        ->delete()
        ->andWhere(sprintf('%s.landing_page = %d', self::ALIAS, $landingPageId))
        ->getQuery()
        ->execute();
    }

    /**
     * Get landing page popular search table name.
     */
    private function getLandingPagePopularSearchTableName()
    {
        return $this->_em->getClassMetadata('FaContentBundle:LandingPagePopularSearch')->getTableName();
    }

    /**
     * Get popular search array for landing page id.
     *
     * @param integer $landingPageId landing page id.
     * @param object  $container Container object.
     *
     * @return array
     */
    public function getPopularSearchArrayByLandingPageId($landingPageId, $container)
    {
        if ($container) {
            $culture     = CommonManager::getCurrentCulture($container);
            $tableName   = $this->getLandingPagePopularSearchTableName();
            $cacheKey    = $tableName.'|'.__FUNCTION__.'|'.$landingPageId.'_'.$culture;
            $cachedValue = CommonManager::getCacheVersion($container, $cacheKey);

            if ($cachedValue !== false) {
                return $cachedValue;
            }
        }
        $popularSearchArray = array();
        $popularSearches = $this->createQueryBuilder(self::ALIAS)
        ->andWhere(sprintf('%s.landing_page = %d', self::ALIAS, $landingPageId))
        ->getQuery()
        ->execute();

        foreach ($popularSearches as $popularSearch) {
            $popularSearchArray[] = array('title' => $popularSearch->getTitle(), 'url' => $popularSearch->getUrl());
        }

        if ($container && count($popularSearchArray)) {
            CommonManager::setCacheVersion($container, $cacheKey, $popularSearchArray);
        }

        return $popularSearchArray;
    }
}
