namespace Fa\Bundle\MessageBundle\Walker;

use Doctrine\ORM\Query\SqlWalker;
use Fa\Bundle\MessageBundle\Repository\MessageRepository;

/**
 * Message count sql walker.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 *
 * @author Samir Amrutya <samiram@aspl.in>
 * @copyright 2014 Friday Media Group Ltd
 * @version 1.0
 */
class MessageCountSqlWalker extends SqlWalker
{
    /**
     * Walks down a walkSelectClause AST node, thereby generating the appropriate SQL.
     *
     * @param $selectClause
     *
     * @return string The SQL.
     */
    public function walkSelectClause($selectClause)
    {
        $sql = parent::walkSelectClause($selectClause);
        $tablealias = $this->getSQLTableAlias($this->getEntityManager()->getClassMetadata('FaMessageBundle:Message')->getTableName(), MessageRepository::ALIAS);

        if ($this->getQuery()->getHint('messageCountSqlWalkerSqlWalker.replaceCountField') === true) {
            $sql = str_replace("count(DISTINCT ".$tablealias.".id)", "count(DISTINCT ".$tablealias.".message_ad_id)", $sql);
        }

        return $sql;
    }
}
